import React from "react";
import Page from "../pages/Page";
import { connect } from "react-redux";
import { updateChefProfile } from "../actions/index";
import RecipeCard from "../components/RecipeCard";

function AuthChefProfile(props) {
  return (
    <>
      <Page>
        <div>
          <img>{props.chefs.profileImg}</img>
          <h2>{props.chefs.name}</h2>
          <p>{props.chefs.title}</p>
          <p>{props.chefs.location}</p>
        </div>
        <div>
          <h3>About:</h3>
          <p>{props.chefs.about}</p>
        </div>
        <div>
          {props.chefs.recipes.map(recipe => (
            <RecipeCard
              key={recipe.id}
              img={recipe.img}
              name={recipe.name}
              ingredients={recipe.ingredients}
              instructions={recipe.instructions}
            />
          ))}
        </div>
        <button>Create Post</button>
        <button>Edit Profile</button>
      </Page>
    </>
  );
}

const mapStateToProps = state => {
  return {
    chefs: state.chefs,
    updatingChef: state.updateChef
  };
};

export default connect(mapStateToProps, { updateChefProfile })(AuthChefProfile);
