import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import axios from "axios";

const userSchema = Yup.object().shape({
  username: Yup.string()
    .required()
    .min(2),
  password: Yup.string()
    .required()
    .min(3)
});

function SignIn(props) {
  const [users, setUsers] = useState([]);

  return (
    <>
      <h1>Sign In</h1>

      <Formik
        initialValues={{
          username: "",
          password: ""
        }}
        onSubmit={(values, tools) => {
          tools.resetForm();
          axios
            .post("https://reqres.in/api/users", values)
            .then(res => {
              console.log("SUCCESSFUL", res);
              console.log(res.data);

              const userData = res.data;

              setUsers([...users, userData]);
            })
            .catch(err => {
              console.log("FAILURE: ", err);
            });
        }}
        validationSchema={userSchema}
        render={props => {
          return (
            <Form className="formContainer">
              <label>
                *Username:
                <Field
                  className="input inputName"
                  name="username"
                  type="text"
                  placeholder="enter username"
                />
              </label>
              {props.errors.username && props.touched.username ? (
                <span className="red">{props.errors.username}</span>
              ) : (
                ""
              )}

              <label>
                *Password:
                <Field
                  className="input inputPassword"
                  name="password"
                  type="text"
                  placeholder="enter password"
                />
              </label>
              {props.errors.password && props.touched.password ? (
                <span className="red">{props.errors.password}</span>
              ) : (
                ""
              )}

              <input className="submitButton" type="submit" />

              <button className="signUpSwitch">
                Don't have an account? Sign up!
              </button>

              <Link to="/">
                <button className="guestSignIn">
                  Just wanna browse? Continue as a guest.
                </button>
              </Link>
            </Form>
          );
        }}
      />
    </>
  );
}

export default SignIn;
