//renders a complete view of one recipe, with a photo, ingredients list, instructions.
// two different views depending on user type
// guest user: buttons to go back to Guest Homepage (recipes) or to Meet The Chefs
// chef user: buttons to Create New Post or view My Profile

import React, { useEffect, useState } from "react";
import Page from "../pages/Page";
import { connect } from "react-redux";
import { getARecipe } from "../actions/index";
import RPAuthButtons from "../components/RPAuthButtons";
import RPUnauthButtons from "../components/RPUnauthButtons";

function RecipePage(props) {
  console.log(props);

  useEffect(() => {
    props.getARecipe(props.match.params.id);
  }, []);

  const token = localStorage.getItem("token");

  return (
    <>
      <Page>
        <h2>{props.recipe_name}</h2>
        <div>
          <img>{props.authorImg}</img>
          <p>Chef {recipe.authorName}</p>
        </div>
        <img>{recipe.img}</img>
        <div>
          <h4>Let's Cook!</h4>
          <p>Approximately {recipe.time} hours</p>
        </div>
        <div>
          <div>
            <h2>Ingredients</h2>
            <p>{recipe.ingredients}</p>
          </div>
          <div>
            <h2>Instructions</h2>
            <p>{recipe.instructions}</p>
          </div>
        </div>
        <div>
          <h4>Recipe Media</h4>
          <div>
            <img />
            <img />
            <img />
            <img />
            <img />
            <img />
            <img />
            <img />
          </div>
        </div>

        <div>{token ? <RPAuthButtons /> : <RPUnauthButtons />}</div>
      </Page>
    </>
  );
}

const mapStateToProps = state => {
  return {
    recipes: state.recipes.recipes,
    error: state.error
  };
};

export default connect(mapStateToProps, { getARecipe })(RecipePage);

