//renders a complete view of one recipe, with a photo, ingredients list, instructions.
// two different views depending on user type
// guest user: buttons to go back to Guest Homepage (recipes) or to Meet The Chefs
// chef user: buttons to Create New Post or view My Profile

import React, { useEffect, useState } from "react";
import Page from "../pages/Page";
import RPAuthButtons from "../components/RPAuthButtons";
import RPUnauthButtons from "../components/RPUnauthButtons";
import axios from "axios";
import { Link } from "react-router-dom";

function RecipePage(props) {
  console.log(props);
  const [recipe, setRecipe] = useState({});

  useEffect(() => {
    axios
      .get(
        `https://chef-2.herokuapp.com/api/recipes/recipeid/${props.match.params.id}`
      )
      .then(res => {
        console.log(res);
        setRecipe(res.data);
      })
      .catch(err => console.log(err));
  }, [props.match.params.id]);

  const token = localStorage.getItem("token");
  const user = localStorage.getItem("user");

  const deleteRecipe = () => {
    axios
      .delete(`https://chef-2.herokuapp.com/api/recipes/delete/${props.id}`)
      .then(res => {
        console.log(res);
        let newRecipes = props.recipes.filter(recipe => {
          return recipe.id !== props.id;
        });
        props.setRecipes(newRecipes);
      })
      .catch(err => {
        console.log(err);
      });
  };

  return (
    <>
      <Page>
        <h2>{recipe.recipe_name}</h2>
        <div>
          <p>Chef {recipe.chef_name}</p>
        </div>
        <img src={recipe.recipe_photo}></img>
        <div>
          <h4>Let's Cook!</h4>
          <p>Approximately {recipe.cook_time} </p>
          <p>Servings: {recipe.servings}</p>
        </div>
        <div>
          <div>
            <h2>Ingredients</h2>
            <p>{recipe.recipe_ingredients}</p>
          </div>
          <div>
            <h2>Instructions</h2>
            <p>{recipe.instructions}</p>
          </div>
        </div>
        <div>
          <h4>Recipe Media</h4>
          <div>
            <img alt="" />
            <img alt="" />
            <img alt="" />
            <img alt="" />
            <img alt="" />
            <img alt="" />
            <img alt="" />
            <img alt="" />
          </div>
        </div>
        {token ? (
          <button className="greenButton" onClick={deleteRecipe}>
            Delete Recipe
          </button>
        ) : (
          ""
        )}
        {token ? (
          <Link to={`/edit-recipe/${props.id}`}>
            <button className="greenButton">Edit Recipe</button>
          </Link>
        ) : (
          ""
        )}

        {/* <div>{token ? <RPAuthButtons /> : <RPUnauthButtons />}</div> */}
      </Page>
    </>
  );
}

export default RecipePage;
